INSERT INTO `boxes` VALUES ('3','<?php\r\nglobal $base_url;\r\n\r\n// Check that the current URL is for a specific node:\r\nif(arg(0) == \'node\' && is_numeric(arg(1))) {\r\n  $nid = arg(1);\r\n  return \'<a href=\"\'.$base_url.\'/og/users/\'.$nid.\'/groupadmin\">Administer Group</a><br />\'.l(\"KOL1\", \"http://kol1.ozmomed.com\");\r\n} else { // Whatever it is we\'re looking at, it\'s not a node\r\n  return NULL; // Return an invalid NID\r\n}','Group Admin LInk','3'),('5','<div id=\"footer_logo\" class=\"clear-block\">powered by <img src=\"/sites/all/themes/ozmo_base/images/ozmosis_logo.png\" /></div>\r\n<div id=\"copyright\">Copyright 2011 Ozmosis. All Rights Reserved</div>','footer_logo','2'),('8','<?php\r\nprint drupal_get_form(\'ozmosis_filter_all_form\');\r\n?>','Events filter block','3'),('9','<?php global $user;\r\n\r\nif(arg(1) == \'edit\') {\r\nprint l(\'View Profile\', \'user/\'.$user->uid, array(\'attributes\' => array(\'id\' => \'profile-sidebar-link\')));\r\n} else {\r\nprint \'<h3>Manage Your Account</h3>\';\r\nprint l(\'Edit My Profile\', \'user/edit/\'.$user->uid, array(\'attributes\' => array(\'id\' => \'profile-sidebar-link\')));\r\nprint l(\'Edit My Picture\', \'user/edit-picture/\'.$user->uid, array(\'attributes\' => array(\'id\' => \'picture-sidebar-link\')));\r\nprint l(\'Change Preferences\', \'config\', array(\'attributes\' => array(\'id\' => \'preferences-sidebar-link\')));\r\n}','Edit Profile Link','3'),('10','<?php\r\n  global $user;\r\n  include_once drupal_get_path(\'module\', \'ozmo_profiles\').\'/classes/NetworkAnalysis.class.php\';\r\n\r\n  $network = new NetworkAnalysis($user->uid);\r\n\r\n  $network->loadRelationsByDegree(1);\r\n  $relationships = $network->getUserIdsByDegree(1);\r\n\r\n  if((arg(0) == \'user\' || arg(0) == \'users\') && is_numeric(arg(1))) {\r\n    $account_id = arg(1);\r\n  }\r\n\r\n  $in_network = in_array($account_id, $relationships) ? TRUE : FALSE;\r\n  $sql = \"\r\n        SELECT rid\r\n        FROM user_relationships\r\n        WHERE approved = 0\r\n        AND  \r\n        (requester_id = %d AND requestee_id = %d)  \r\n        OR (requestee_id = %d AND requester_id = %d)\r\n      \";\r\n  $pending = db_result(db_query($sql, $user->uid, $account_id, $user->uid, $account_id));\r\n\r\n  print \'<ul>\';\r\n//  print \'<li>\'.l(\'Send this user a message\', \'messages/new/\'.arg(1), array(\'query\' => array(\'destination\' => \'user/\'.arg(1)),)).\'</li>\';\r\n  print \'<li>\';\r\n  print l(\'View discussions submitted by this user\', \'discussions\', array(\'query\'=>array(\'oz_filters\'=>base64_encode(serialize(array(\'uid\' => arg(1)))),\'quicktabs_ouzo_page_9\'=>\'ouzo_tab_15\')));\r\n  print \'</li>\';\r\n\r\n  if($in_network == FALSE && $pending == FALSE) {\r\n    print \'<li>\';\r\n    print l(\'Add this user to your network\', \'relationship/\'.arg(1).\'/request/2\', array(\'query\' => array(\'destination\' => \'user/\'.arg(1)), \'attributes\' => array(\'class\' => \'user_relationships_popup_link\')));\r\n    print \'</li>\';\r\n  }\r\n\r\n//check for an existing subscription\r\n  $subscriptions = notifications_get_subscriptions(array(\'type\' => \'author\', \'uid\' => $user->uid), array(\'author\' => $account_id));\r\n\r\n  if($subscriptions){\r\n    print \'<li id=\"user-actions-unsubscribe\"><a href=\"#\">Unsubscribe from this user`s content</a></li>\';\r\n  } else {\r\n    print \'<li id=\"user-actions-subscribe\"><a href=\"#\">Subscribe to this user`s content</a></li>\';\r\n  }\r\n\r\n// Add link to edit a user\'s profile if we are an admin\r\n  if(in_array(\'admin\', $user->roles)) {\r\n    print \'<li>\';\r\n    print l(\'Edit this users profile\', \'user/edit/\'.arg(1), array(\'query\' => array(\'destination\' => \'user/\'.arg(1)),));\r\n    print \'</li>\';\r\n  }\r\n  print \'</ul>\';\r\n','User Actions','3');
INSERT INTO `boxes` VALUES ('11','<?php global $account; ?>\r\n<ul>\r\n	<li>\r\n<?php print l(\'Add this user to your network\', \'relationships/\'.$account->uid); ?>	</li>\r\n</ul>\r\n','Ozmo User Actions','3'),('12','<?php\r\n\r\n  $node = node_load(arg(1));\r\n\r\n  $term_ids = array();\r\n if(is_array($node->taxonomy)) {\r\n    foreach($node->taxonomy as $term) {\r\n      $term_ids[] = $term->tid;\r\n    }\r\n  }\r\n  $view_args = array();\r\n  if($term_ids) {\r\n    $view_args[] = implode(\'+\', $term_ids);\r\n  }\r\n\r\n  $display_id = \'block_1\';\r\n  $view = views_get_view(\'related_articles\');\r\n       if (!empty($view)) {\r\n        $view_content = $view->execute_display($display_id , $view_args);\r\nif($view_content) {\r\n        print \'<div class=\"related-content-block rounded\"><h2 class=\"related-content-type-heading\">\'.$view_content[\'subject\'].\'</h2>\'.$view_content[\'content\'].\'</div>\';\r\n}\r\n  }\r\n\r\n  $display_id = \'block_2\';\r\n  $view = views_get_view(\'related_articles\');\r\n       if (!empty($view)) {\r\n        $view_content = $view->execute_display($display_id , $view_args);\r\nif($view_content) {\r\n        print \'<div class=\"related-content-block rounded\"><h2 class=\"related-content-type-heading\">\'.$view_content[\'subject\'].\'</h2>\'.$view_content[\'content\'].\'</div>\';\r\n}\r\n  }\r\n\r\n  $display_id = \'block_3\';\r\n  $view = views_get_view(\'related_articles\');\r\n       if (!empty($view)) {\r\n        $view_content = $view->execute_display($display_id , $view_args);\r\nif($view_content) {\r\n        print \'<div class=\"related-content-block rounded\"><h2 class=\"related-content-type-heading\">\'.$view_content[\'subject\'].\'</h2>\'.$view_content[\'content\'].\'</div>\';\r\n}\r\n  }','related_content_wrapper','3'),('13','<?php \r\n  global $user; \r\n  $node = node_load(arg(1)); \r\n\r\n  $edit_own_permission_string = \'edit own \'.$node->type.\' content\';\r\n\r\n  //check for an existing subscription\r\n $subscriptions = notifications_get_subscriptions(array(\'type\' => \'thread\', \'uid\' => $user->uid), array(\'nid\' => $node->nid));\r\n?>\r\n<h2 class=\"title\">Tools</h2>\r\n<ul>\r\n<?php /*<li id=\"content-actions-share\"><a href=\"#\">Share</a></li> */ ?>\r\n\r\n<?php if($subscriptions) : ?>\r\n  <li id=\"content-actions-unsubscribe\"><a href=\"#\">Unsubscribe</a></li>\r\n<?php else: ?>\r\n  <li id=\"content-actions-subscribe\"><a href=\"#\">Subscribe</a></li>\r\n<?php endif; ?>\r\n<!-- <li id=\"content-actions-bookmark\"><a href=\"#\">Bookmark</a></li> -->\r\n<?php if((user_access($edit_own_permission_string, $user) && ($node->uid == $user->uid)) || user_access(\'administer nodes\', $user)) : ?>\r\n\r\n<?php // if(user_access($edit_own_permission_string, $user)): ?>\r\n<!-- <li id=\"content-actions-edit\"><?php print l(\'Edit\', \'node/\'.$node->nid.\'/edit\'); ?></li> -->\r\n<?php endif; ?>\r\n<li id=\"content-actions-print\"><?php print l(\'Print\', \'print/\'.$node->nid); ?></li>\r\n</ul>\r\n','Content Actions','3');
